export default class Todo{constructor(){this.enterButton=document.querySelector("#inputArea button"),this.input=document.querySelector("#inputArea input"),this.ul=document.querySelector("ul#toDoList"),this.enterButton.addEventListener("click",t=>this.addListItem(t)),this.input.addEventListener("keypress",t=>this.addListItem(t))}addListItem(t){this.input.value.length>0&&("Enter"===t.key||void 0===t.key)&&this.createListItem()}createListItem(){const t=document.createElement("li");t.innerHTML=this.input.value+' <i class="far fa-trash-alt"></i>',this.ul.appendChild(t),this.input.value="",t.addEventListener("click",t=>this.crossOut(t)),t.querySelector("i").addEventListener("click",t=>this.deleteListItem(t))}crossOut(t){t.currentTarget.classList.toggle("done")}deleteListItem(t){t.stopPropagation();t.currentTarget.parentNode.remove()}}